package paqueteitla;

public class Ejercicio_7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//7.	Declarar un entero = 0. Mientras el entero <5, imprimir su valor y incrementarlo de 1. 
		int i=0;
		
		while(i<5) {
			
			System.out.println(i);
			
			i++;
		}

	}

}
